            Handkarten = new List<Karte>();
        }

        public bool hatHandkarten()
        {
            if (Handkarten.Count > 0)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        public Karte startKarteLegen()
        {
            if (Handkarten.Count > 0)
            {
                Karte antwortKarte = Handkarten[0];
                Handkarten.RemoveAt(0);
                return antwortKarte;
            }
            return null;
        }

        /// <summary>
        /// Diese Mehtode checkt, ob und welche Karte ein Spieler auf eine bereits gelegte Karte antworten kann und wird.
        /// </summary>
        /// <param name="card"></param>
        /// <returns></returns>
        public Karte legeKarte(Karte card)
        {
            List<Karte> möglicheAntwortKarten = Handkarten.FindAll(x => x.Kartenfarbe == card.Kartenfarbe);

            if (möglicheAntwortKarten.Count == 0) //wenn ich keine passende Farbe auf der Hand habe, dann schau ich, ob ich die Farbe wechseln kann
            {
                möglicheAntwortKarten = Handkarten.FindAll(x => x.Kartentyp == card.Kartentyp);

                if (möglicheAntwortKarten.Count == 0)
                {
                    //Wenn ich auch die Farbe nicht wechseln kann, dann muss ich aussetzen.
                    return null; //null bedeutet hier, dass dieser Spieler keine Karte legen kann.
                }
            }
            Karte antwortKarte = möglicheAntwortKarten.First();//karte kurz merken, weil wir sie gleich aus den Handkarten rauslöschen
            Handkarten.Remove(möglicheAntwortKarten.First()); // hier nehmen wir dem Spieler dann die Handkarte weg.
            return antwortKarte; //hier sind wir sicher, dass mindestens eine Karte möglich ist, und die geben wir zurück.


        }
